    }
  }

  private onEvent(event) {
    if (this.readyState === State.OPEN && this.onmessage) {
      this.onmessage({ data: event });
    }
  }

  private onActivity() {
    if (this.onactivity) {
      this.onactivity();
    }
  }

  private onError(error) {
    if (this.onerror) {
      this.onerror(error);
    }
  }

  private openStream() {
    this.stream = Runtime.createSocketRequest(
      'POST',
      getUniqueURL(this.hooks.getReceiveURL(this.location, this.session))
    );

    this.stream.bind('chunk', chunk => {
      this.onChunk(chunk);
    });
    this.stream.bind('finished', status => {
      this.hooks.onFinished(this, status);
    });
    this.stream.bind('buffer_too_long', () => {
      this.reconnect();
    });

    try {
      this.stream.start();
    } catch (error) {
      Util.defer(() => {
        this.onError(error);
        this.onClose(1006, 'Could not start streaming', false);
      });
    }
  }

  private closeStream() {
    if (this.stream) {
      this.stream.unbind_all();
      this.stream.close();
      this.stream = null;
    }
  }
}

function getLocation(url): URLLocation {
  var parts = /([^\?]*)\/*(\??.*)/.exec(url);
  return {
    base: parts[1],
    queryString: parts[2]
  };
}

function getSendURL(url: URLLocation, session: string): string {
  return url.base + '/' + session + '/xhr_send';
}

function getUniqueURL(url: string): string {
  var separator = url.indexOf('?') === -1 ? '?' : '&';
  return url + separator + 't=' + +new Date() + '&n=' + autoIncrement++;
}

function replaceHost(url: string, hostname: string): string {
  var urlParts = /(https?:\/\/)([^\/:]+)((\/|:)?.*)/.exec(url);
  return urlParts[1] + hostname + urlParts[3];
}

function randomNumber(max: number): number {
  return Runtime.randomInt(max);
}

function randomString(length: number): string {
  var result = [];

  for (var i = 0; i < length; i++) {
    result.push(randomNumber(32).toString(32));
  }

  return result.join('');
}

