    options = Object.assign(
      { ignoreNullOrigin: config.ignoreNullOrigin },
      options
    );

    transport = new TransportStrategy(
      name,
      priority,
      manager ? manager.getAssistant(transportClass) : transportClass,
      options
    );
  } else {
    transport = UnsupportedStrategy;
  }

  return transport;
};

